                    case "none":
                        g = "" + a[b[c][0]];
                        break;
                    case "currency":
                        g = a.valueToCurrencyString(a[b[c][0]]);
                        break;
                    case "percentage":
                        g = a.valueToPercentageString(a[b[c][0]]);
                        break;
                    default:
                        g = "" + a[b[c][0]]
                }
                a[e][n].innerHTML = "" + g
            }
    };
    a.updateCartView = function() {
        var b = [],
